mapper.update:
UPDATE t_user
SET authCd = null
WHERE email = #{email} AND authCd = #{authCd}

mapper.select:
SELECT *
FROM t_user
WHERE email = #{email}
AND provider = #{provider}
AND authCd IS NULL

